ProgressBar.prototype.setPercent = function(pct) {

    if (pct == this.lastValue)
        return;

    this.lastValue = pct;

    if (pct >= 99)
        this.bg.style.visibility = "hidden";
    else {
        this.bg.style.visibility = "visible";
        this.fg.style.width = (this.widthScale * pct * 0.01) + "px";
    }
};


