/**
 * Converts seconds into Hours:Minutes:Seconds String
 * @param {Number} time in seconds
 * @returns {string}
 * @private
 */
function convertSecsToHMS(time) {
    var sign = "";
    if (time < 0) {sign="-"; time = -time;}
    var hrs = ~~(time / 3600);
    var mins = ~~((time % 3600) / 60);
    var secs = time % 60;
    var ret = sign;
    if (hrs > 0)
        ret += hrs + ":" + (mins < 10 ? "0" : "");
    ret += mins + ":" + (secs < 10 ? "0" : "");
    ret += secs.toFixed(2);
    return ret;
}

