            var X0 = this.nearestPointInPointToLine(point, lineStart, lineEnd);
            var X1 = this.nearestPointInPointToLine(intersectPoint, lineStart, lineEnd);

            var p1 = new THREE.Vector3();
            p1.subVectors(X1, X0);
